<style lang="scss">
  .purchase-nav-item {
    &__icon {
      opacity: 0.1;
      svg {
        fill: #4F4F4F;
      }
    }

    &:hover &__icon {
      opacity: 1;
      svg {
        fill: #6AD091;
      }
    }

    &__decor {
      position: absolute;
      width: 225px;
      height: 180px;
      right: 0;
      bottom: 0;
      z-index: 0;

      svg {
        width: 100%;
        height: 100%;
        stroke: theme('colors.text01');
        fill: none;
        opacity: 0.1;
      }
    }

    &:hover &__decor {
      svg {
        stroke: theme('colors.primary-hover');
        opacity: 1;
      }
    }
  }
</style>

<style scoped lang="scss">
  .nav-items {
    display: flex;
    flex-wrap: wrap;
    margin: -10px;

    &__item {
      margin: 10px;
      height: 212px;
      width: calc(50% - 20px);
    }
  }

  .purchase-nav-item {
    position: relative;
    display: flex;
    flex-direction: column;
    gap: 16px;
    border-radius: 8px;
    padding: 24px;
    @apply tw-bg-base01 tw-text-text00;

    &:hover {
      @apply tw-bg-primary tw-text-white;
    }

    &__name {
      position: relative;
      z-index: 1;
      margin-top: auto;
      font-size: 32px;
      line-height: 1.375;
    }
  }
</style>
